export function makePlasmicAuth(user: string, projectApiToken: string, projectHost?: string): string {
  // {
  //   "host": "${projectHost}",
  //   "user": "admin@example.com",
  //   "token": "${projectApiToken}"
  // }

  return `{
  "user": "${user}",
  "token": "${projectApiToken}",
  "host": "${projectHost || "https://api.squirrel.dev"}"
}`;
}
